import "./App.css";
import ContentContainer from "./components/ContentContainer";
import CardComponent from "./components/CardComponent";
import GuessComponent from "./components/GuessComponent";
import { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const img_file = ["cat1", "cat2"];
  const [geolocation, setGeo] = useState("");
  const [result, setResult] = useState("");

  useEffect(() => {
    console.log(result);
  });

  useEffect(() => {
    const fetchIp = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:5000/api/get_ip");
        const continent_api = await axios.get(
          `https://restcountries.com/v3.1/alpha/${res.data.country}`
        );
        const geo = res.data;
        geo["continent"] = continent_api.data[0].subregion;
        geo["country"] = continent_api.data[0].name.common;
        setGeo(geo);
      } catch (error) {
        console.log("Error fetching ip address: ", error);
      }
    };
    fetchIp();
  }, []);
  console.log(geolocation);

  const continent = geolocation.continent;
  const country = geolocation.country;
  const region = geolocation.region;
  const city = geolocation.city;

  return (
    <div className="App">
      <ContentContainer>
        <GuessComponent
          geo_guess={{ continent: "test" }}
          setResult={setResult}
        />
        {/* <CardComponent img_file={img_file[0]} />
        <CardComponent img_file={img_file[1]} /> */}
      </ContentContainer>
    </div>
  );
}

export default App;
